import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Check, Clock } from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface ComplianceRulesProps {
  rules: any[];
}

export default function ComplianceRules({ rules }: ComplianceRulesProps) {
  const queryClient = useQueryClient();

  const toggleRule = useMutation({
    mutationFn: async ({ id, isActive }: { id: string; isActive: boolean }) => {
      return apiRequest('PATCH', `/api/compliance/rules/${id}`, { isActive: !isActive });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard'] });
    }
  });

  const formatLastTriggered = (lastTriggered: string | null) => {
    if (!lastTriggered) return 'Never';

    const now = new Date();
    const triggeredTime = new Date(lastTriggered);
    const diffMs = now.getTime() - triggeredTime.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));

    if (diffMins < 60) return `${diffMins} min ago`;

    const diffHours = Math.floor(diffMins / 60);
    if (diffHours < 24) return `${diffHours} hour${diffHours !== 1 ? 's' : ''} ago`;

    const diffDays = Math.floor(diffHours / 24);
    return `${diffDays} day${diffDays !== 1 ? 's' : ''} ago`;
  };

  const getRuleTypeColor = (ruleType: string) => {
    switch (ruleType) {
      case 'rate_limit':
        return 'bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-300';
      case 'pii_detection':
        return 'bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-300';
      case 'gdpr_consent':
        return 'bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-300';
      default:
        return 'bg-slate-100 text-slate-700 dark:bg-slate-700/30 dark:text-slate-300';
    }
  };

  return (
    <Card className="border border-slate-200/60 bg-white/70 backdrop-blur-sm shadow-sm dark:bg-slate-800/70 dark:border-slate-700/60" data-testid="compliance-rules">
      <div className="p-4 border-b border-slate-200/60 dark:border-slate-700/60">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold text-slate-900 dark:text-white">Compliance Rules</h2>
          <span className="text-sm text-slate-500 dark:text-slate-400">{rules.length} rules configured</span>
        </div>
      </div>
      <CardContent className="p-4">
        <div className="space-y-3" data-testid="rules-list">
          {rules.map((rule, index) => (
            <div 
              key={rule.id || index} 
              className="border border-slate-200/60 rounded-lg p-3 bg-slate-50/50 dark:bg-slate-800/50 dark:border-slate-700/60"
              data-testid={`rule-${index}`}
            >
              <div className="flex items-center justify-between mb-2">
                <div className="flex items-center space-x-3">
                  <h3 className="text-sm font-medium text-slate-900 dark:text-white" data-testid={`text-rule-name-${index}`}>
                    {rule.name}
                  </h3>
                  <Badge 
                    variant="secondary" 
                    className={`${getRuleTypeColor(rule.ruleType)} text-xs px-2 py-1`}
                    data-testid={`badge-rule-type-${index}`}
                  >
                    {rule.ruleType?.replace('_', ' ').toUpperCase()}
                  </Badge>
                </div>
                <div className="flex items-center space-x-3">
                  <button
                    onClick={() => toggleRule.mutate({ id: rule.id, isActive: rule.isActive })}
                    disabled={toggleRule.isPending}
                    data-testid={`button-toggle-rule-${index}`}
                    className={`
                      relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:ring-offset-2 disabled:opacity-50
                      ${rule.isActive 
                        ? 'bg-emerald-600 hover:bg-emerald-700' 
                        : 'bg-slate-300 hover:bg-slate-400 dark:bg-slate-600 dark:hover:bg-slate-500'
                      }
                    `}
                  >
                    <span className="sr-only">Toggle rule</span>
                    <span
                      className={`
                        inline-block h-4 w-4 transform rounded-full bg-white transition-transform
                        ${rule.isActive ? 'translate-x-6' : 'translate-x-1'}
                      `}
                    />
                  </button>
                  <span className={`text-xs font-medium ${
                    rule.isActive 
                      ? 'text-emerald-600 dark:text-emerald-400' 
                      : 'text-slate-600 dark:text-slate-400'
                  }`}>
                    {rule.isActive ? 'ON' : 'OFF'}
                  </span>
                </div>
              </div>

              <p className="text-sm text-slate-600 dark:text-slate-400 mb-2" data-testid={`text-rule-description-${index}`}>
                {rule.description}
              </p>

              <div className="flex items-center text-xs text-slate-500 dark:text-slate-500">
                <Clock className="h-3 w-3 mr-1" />
                <span data-testid={`text-rule-last-triggered-${index}`}>
                  Last triggered: {formatLastTriggered(rule.lastTriggered)}
                </span>
              </div>
            </div>
          ))}

          {rules.length === 0 && (
            <div className="text-center py-6 text-slate-500 dark:text-slate-400" data-testid="no-rules">
              <Check className="h-8 w-8 text-slate-300 dark:text-slate-600 mx-auto mb-2" />
              <p>No compliance rules configured</p>
              <p className="text-sm">System ready for compliance monitoring</p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/DataClassification.content:
import { Card, CardContent } from "@/components/ui/card";
import { AlertTriangle, AlertCircle, Info } from "lucide-react";

interface DataClassificationProps {
  classifications: any[];
}

export default function DataClassification({ classifications }: DataClassificationProps) {
  const getRiskCounts = () => {
    return classifications.reduce((acc, item) => {
      acc[item.riskLevel] = (acc[item.riskLevel] || 0) + 1;
      return acc;
    }, { high: 0, medium: 0, low: 0 });
  };

  const getRecentClassifications = () => {
    return classifications
      .sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime())
      .slice(0, 5);
  };

  const formatTimeAgo = (timestamp: string) => {
    const now = new Date();
    const classTime = new Date(timestamp);
    const diffMs = now.getTime() - classTime.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));

    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;

    const diffHours = Math.floor(diffMins / 60);
    if (diffHours < 24) return `${diffHours}h ago`;

    const diffDays = Math.floor(diffHours / 24);
    return `${diffDays}d ago`;
  };

  const riskCounts = getRiskCounts();
  const recentClassifications = getRecentClassifications();

  return (
    <Card className="border border-gray-200" data-testid="data-classification">
      <div className="p-6 border-b border-gray-200">
        <h2 className="text-lg font-semibold text-gray-900">Data Classification</h2>
      </div>
      <CardContent className="p-6">
        {/* Classification Categories */}
        <div className="space-y-4 mb-6" data-testid="risk-level-summary">
          <div className="flex items-center justify-between p-4 bg-red-50 border border-red-200 rounded-lg">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-red-100 rounded-lg flex items-center justify-center">
                <AlertTriangle className="text-red-600 h-5 w-5" />
              </div>
              <div>
                <h3 className="text-sm font-medium text-gray-900">High Risk</h3>
                <p className="text-xs text-gray-600">SSN, Credit Cards</p>
              </div>
            </div>
            <span className="text-lg font-semibold text-red-600" data-testid="text-high-risk-count">
              {riskCounts.high}
            </span>
          </div>

          <div className="flex items-center justify-between p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-yellow-100 rounded-lg flex items-center justify-center">
                <AlertCircle className="text-yellow-600 h-5 w-5" />
              </div>
              <div>
                <h3 className="text-sm font-medium text-gray-900">Medium Risk</h3>
                <p className="text-xs text-gray-600">Email, Phone Numbers</p>
              </div>
            </div>
            <span className="text-lg font-semibold text-yellow-600" data-testid="text-medium-risk-count">
              {riskCounts.medium}
            </span>
          </div>

          <div className="flex items-center justify-between p-4 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                <Info className="text-blue-600 h-5 w-5" />
              </div>
              <div>
                <h3 className="text-sm font-medium text-gray-900">Low Risk</h3>
                <p className="text-xs text-gray-600">Names, Addresses</p>
              </div>
            </div>
            <span className="text-lg font-semibold text-blue-600" data-testid="text-low-risk-count">
              {riskCounts.low}
            </span>
          </div>
        </div>

        {/* Recent Classifications */}
        <div>
          <h3 className="text-sm font-medium text-gray-900 mb-3">Recent Detections</h3>
          <div className="space-y-2" data-testid="recent-detections">
            {recentClassifications.map((item, index) => (
              <div 
                key={item.id || index} 
                className="flex items-center justify-between text-sm"
                data-testid={`detection-${index}`}
              >
                <span className="text-gray-600" data-testid={`text-detection-type-${index}`}>
                  {item.dataType?.replace('_', ' ').replace(/\b\w/g, (l: string) => l.toUpperCase()) || 'Unknown'}
                </span>
                <span className="text-xs text-gray-500" data-testid={`text-detection-time-${index}`}>
                  {formatTimeAgo(item.timestamp)}
                </span>
              </div>
            ))}

            {recentClassifications.length === 0 && (
              <div className="text-center py-4 text-gray-500" data-testid="no-recent-detections">
                <p className="text-sm">No recent detections</p>
              </div>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/IncidentLog.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Download, AlertCircle, AlertTriangle, Info, ExternalLink, X } from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useState } from "react";
import { formatTimeAgoEST, formatFullDateTimeEST } from "@/lib/timeUtils";

interface IncidentLogProps {
  incidents: any[];
}

export default function IncidentLog({ incidents }: IncidentLogProps) {
  const queryClient = useQueryClient();
  const [isAllIncidentsOpen, setIsAllIncidentsOpen] = useState(false);

  const updateIncident = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return apiRequest('PATCH', `/api/incidents/${id}`, updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard'] });
    }
  });

  const exportIncidents = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/incidents/export');
      if (!response.ok) throw new Error('Export failed');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `security_incidents_${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      return { success: true };
    },
    onSuccess: () => {
      console.log(`Exported ${incidents.length} incidents to CSV`);
    }
  });

  const formatTimeAgo = (timestamp: string) => {
    return formatTimeAgoEST(timestamp);
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'critical':
        return <AlertCircle className="h-4 w-4" />;
      case 'warning':
        return <AlertTriangle className="h-4 w-4" />;
      default:
        return <Info className="h-4 w-4" />;
    }
  };

  const getSeverityBorderColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'border-l-4 border-error bg-error/5';
      case 'warning':
        return 'border-l-4 border-warning bg-warning/5';
      default:
        return 'border-l-4 border-blue-500 bg-blue-50';
    }
  };

  const getSeverityTextColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'text-error';
      case 'warning':
        return 'text-warning';
      default:
        return 'text-blue-600';
    }
  };

  const getStatusBadgeColor = (status: string) => {
    switch (status) {
      case 'resolved':
      case 'completed':
        return 'bg-secondary/10 text-secondary';
      case 'investigating':
        return 'bg-gray-100 text-gray-800';
      case 'open':
        return 'bg-error/10 text-error';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'resolved':
        return 'Resolved';
      case 'completed':
        return 'Completed';
      case 'investigating':
        return 'Investigating';
      case 'open':
        return 'Open';
      default:
        return status;
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'warning':
        return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'high':
        return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'resolved':
      case 'completed':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'investigating':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'open':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const recentIncidents = incidents.slice(0, 5);

  const handleExportIncidents = () => {
    try {
      // Create CSV content
      const csvHeaders = ['Date', 'Severity', 'Description', 'Status', 'Source', 'ID'];
      const csvRows = incidents.map(incident => [
        new Date(incident.timestamp).toLocaleString(),
        incident.severity,
        `"${incident.description.replace(/"/g, '""')}"`, // Escape quotes
        incident.status,
        incident.source,
        incident.id
      ]);

      const csvContent = [
        csvHeaders.join(','),
        ...csvRows.map(row => row.join(','))
      ].join('\n');

      // Create and download file
      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const link = document.createElement('a');
      const url = URL.createObjectURL(blob);

      link.setAttribute('href', url);
      link.setAttribute('download', `security_incidents_${new Date().toISOString().split('T')[0]}.csv`);
      link.style.visibility = 'hidden';

      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      console.log(`Exported ${incidents.length} incidents to CSV`);
    } catch (error) {
      console.error('Error exporting incidents:', error);
    }
  };

  return (
    <Card className="border border-gray-200" data-testid="incident-log">
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold text-gray-900">Incident Log</h2>
          <Button 
            variant="ghost"
            size="sm"
            onClick={handleExportIncidents}
            className="text-primary hover:text-primary/80 text-sm font-medium flex items-center space-x-2"
            data-testid="button-export-incidents"
          >
            <Download className="h-4 w-4" />
            <span>Export</span>
          </Button>
        </div>
      </div>
      <CardContent className="p-6">
        <div className="space-y-4" data-testid="incidents-list">
          {recentIncidents.map((incident, index) => (
            <div 
              key={incident.id || index} 
              className={`${getSeverityBorderColor(incident.severity)} p-3 rounded-r-lg`}
              data-testid={`incident-${index}`}
            >
              <div className="flex items-center justify-between mb-1">
                <div className="flex items-center space-x-2">
                  <span className={`text-sm font-medium ${getSeverityTextColor(incident.severity)}`}>
                    {getSeverityIcon(incident.severity)}
                  </span>
                  <span className={`text-sm font-medium ${getSeverityTextColor(incident.severity)}`} data-testid={`text-incident-severity-${index}`}>
                    {incident.severity.charAt(0).toUpperCase() + incident.severity.slice(1)}
                  </span>
                </div>
                <span className="text-xs text-gray-500" data-testid={`text-incident-time-${index}`}>
                  {formatTimeAgo(incident.timestamp)}
                </span>
              </div>

              <p className="text-sm text-gray-700 mb-2" data-testid={`text-incident-description-${index}`}>
                {incident.description}
              </p>

              <div className="flex items-center justify-between">
                <Badge 
                  className={getStatusBadgeColor(incident.status)}
                  data-testid={`badge-incident-status-${index}`}
                >
                  {getStatusLabel(incident.status)}
                </Badge>

                {incident.status === 'open' && (
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => updateIncident.mutate({ 
                      id: incident.id, 
                      updates: { status: 'investigating' } 
                    })}
                    disabled={updateIncident.isPending}
                    className="text-xs"
                    data-testid={`button-update-incident-${index}`}
                  >
                    {updateIncident.isPending ? 'Updating...' : 'Investigate'}
                  </Button>
                )}
              </div>
            </div>
          ))}

          {recentIncidents.length === 0 && (
            <div className="text-center py-8 text-gray-500" data-testid="no-incidents">
              <AlertCircle className="h-12 w-12 text-gray-300 mx-auto mb-2" />
              <p>No recent incidents</p>
              <p className="text-sm">All systems operating normally</p>
            </div>
          )}
        </div>

        {incidents.length > 5 && (
          <div className="mt-6">
            <Dialog open={isAllIncidentsOpen} onOpenChange={setIsAllIncidentsOpen}>
              <DialogTrigger asChild>
                <Button 
                  variant="ghost" 
                  className="w-full text-center text-primary hover:text-primary/80 text-sm font-medium transition-colors"
                  data-testid="button-view-all-incidents"
                >
                  <ExternalLink className="h-4 w-4 mr-2" />
                  View All Incidents ({incidents.length})
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-4xl max-h-[80vh] bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 shadow-xl" aria-describedby="incidents-dialog-description">
                <DialogHeader>
                  <div className="flex items-center justify-between">
                    <DialogTitle className="flex items-center space-x-2">
                      <AlertTriangle className="h-5 w-5" />
                      <span>All Security Incidents ({incidents.length})</span>
                    </DialogTitle>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => exportIncidents.mutate()}
                      disabled={exportIncidents.isPending}
                      className="flex items-center space-x-2"
                    >
                      <Download className="h-4 w-4" />
                      <span>Export CSV</span>
                    </Button>
                  </div>
                  <p id="incidents-dialog-description" className="text-sm text-slate-600 dark:text-slate-400">
                    Complete list of security incidents with details and resolution status
                  </p>
                </DialogHeader>
                <div className="space-y-4 overflow-y-auto max-h-96">
                  {incidents.map((incident, index) => (
                    <div 
                      key={incident.id || index} 
                      className={`p-4 rounded-lg border transition-colors ${getSeverityBorderColor(incident.severity)}`}
                      data-testid={`incident-${index}`}
                    >
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center space-x-2 mb-2">
                            <div className={`flex items-center space-x-1 ${
                              incident.severity === 'critical' ? 'text-error' : 
                              incident.severity === 'warning' ? 'text-warning' : 
                              'text-info'
                            }`}>
                              {getSeverityIcon(incident.severity)}
                              <Badge 
                                className={getSeverityColor(incident.severity)}
                                data-testid={`badge-severity-${index}`}
                              >
                                {incident.severity?.toUpperCase()}
                              </Badge>
                            </div>
                            <span className="text-xs text-slate-500 dark:text-slate-400" data-testid={`text-time-${index}`}>
                              {formatTimeAgo(incident.timestamp)}
                            </span>
                            <Badge 
                              className={getStatusColor(incident.status)}
                              data-testid={`badge-status-${index}`}
                            >
                              {incident.status || 'Open'}
                            </Badge>
                          </div>
                          <h4 className="font-medium text-slate-900 dark:text-white text-sm mb-1" data-testid={`text-description-${index}`}>
                            {incident.description}
                          </h4>
                          {incident.source && (
                            <div className="flex items-center space-x-1 mb-2">
                              <span className="text-xs text-slate-500">Source:</span>
                              <Badge variant="outline" className="text-xs">
                                {incident.source}
                              </Badge>
                            </div>
                          )}
                          {incident.id && (
                            <div className="text-xs text-slate-400 font-mono">
                              ID: {incident.id}
                            </div>
                          )}
                        </div>
                        <div className="flex items-center space-x-2 ml-4">
                          {incident.status !== 'resolved' && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => updateIncident.mutate({ 
                                id: incident.id, 
                                updates: { status: 'resolved' } 
                              })}
                              disabled={updateIncident.isPending}
                              className="text-xs"
                              data-testid={`button-resolve-${index}`}
                            >
                              Mark Resolved
                            </Button>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </DialogContent>
            </Dialog>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/LLMResponseMonitor.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Switch } from "@/components/ui/switch";
import { Settings, AlertTriangle, Shield, Eye, FileText, TestTube } from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useState } from "react";

interface LLMResponseMonitorProps {
  violations: any[];
  stats: any;
}

export default function LLMResponseMonitor({ violations, stats }: LLMResponseMonitorProps) {
  const queryClient = useQueryClient();
  const [isConfigOpen, setIsConfigOpen] = useState(false);
  const [scanSettings, setScanSettings] = useState({
    enableFinancialAdviceDetection: true,
    enablePIIDetection: true,
    enableUnverifiedDataDetection: true,
    autoBlockViolations: true,
    realtimeScanning: true
  });

  const scanResponse = useMutation({
    mutationFn: async (content: string) => {
      return apiRequest('POST', '/api/llm/scan', { content });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard'] });
    }
  });

  const testLLMResponse = useMutation({
    mutationFn: async (testType: string) => {
      const testPrompts = {
        financial: "You should invest all your money in Bitcoin right now for guaranteed returns!",
        pii: "Here's my social security number: 123-45-6789 and credit card: 4532-1234-5678-9012",
        unverified: "According to my insider sources, this company will announce major news tomorrow"
      };
      return apiRequest('POST', '/api/llm/generate', { 
        prompt: testPrompts[testType as keyof typeof testPrompts], 
        type: 'test' 
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard'] });
    }
  });

  const formatTimeAgo = (timestamp: string) => {
    const now = new Date();
    const violationTime = new Date(timestamp);
    const diffMs = now.getTime() - violationTime.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));

    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins} minute${diffMins !== 1 ? 's' : ''} ago`;

    const diffHours = Math.floor(diffMins / 60);
    if (diffHours < 24) return `${diffHours} hour${diffHours !== 1 ? 's' : ''} ago`;

    const diffDays = Math.floor(diffHours / 24);
    return `${diffDays} day${diffDays !== 1 ? 's' : ''} ago`;
  };

  const getViolationBadgeColor = (violationType: string) => {
    switch (violationType) {
      case 'financial_advice':
        return 'bg-error/10 text-error';
      case 'unverified_data':
        return 'bg-warning/10 text-warning';
      case 'pii_exposure':
        return 'bg-error/10 text-error';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getViolationLabel = (violationType: string) => {
    switch (violationType) {
      case 'financial_advice':
        return 'Financial Advice';
      case 'unverified_data':
        return 'Unverified Data';
      case 'pii_exposure':
        return 'PII Exposure';
      default:
        return violationType.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
    }
  };

  const getActionBadgeColor = (action: string) => {
    switch (action) {
      case 'blocked':
        return 'bg-error/10 text-error';
      case 'rewritten':
        return 'bg-warning/10 text-warning';
      case 'flagged':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const recentViolations = violations.slice(0, 5);

  return (
    <Card className="border border-gray-200" data-testid="llm-response-monitor">
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold text-gray-900">LLM Response Monitor</h2>
          <Dialog open={isConfigOpen} onOpenChange={setIsConfigOpen}>
            <DialogTrigger asChild>
              <Button 
                variant="outline" 
                size="sm"
                className="flex items-center space-x-2"
                data-testid="button-configure-scanning"
              >
                <Settings className="h-4 w-4" />
                <span>Configure</span>
              </Button>
            </DialogTrigger>
