import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertTriangle, CheckCircle, Clock, XCircle, Shield, Bell } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface SecurityAlert {
  id: string;
  title: string;
  description: string;
  severity: "low" | "medium" | "high" | "critical";
  source: string;
  status: "active" | "acknowledged" | "resolved";
  timestamp: string;
  metadata?: any;
}

export default function AlertsPage() {
  const [filter, setFilter] = useState<"all" | "active" | "critical">("all");

  const { data: alerts, isLoading } = useQuery({
    queryKey: ["/api/alerts"],
    refetchInterval: 5000, // Refresh every 5 seconds
  });

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case "critical":
        return <XCircle className="h-4 w-4 text-red-500" />;
      case "high":
        return <AlertTriangle className="h-4 w-4 text-orange-500" />;
      case "medium":
        return <Clock className="h-4 w-4 text-yellow-500" />;
      case "low":
        return <CheckCircle className="h-4 w-4 text-blue-500" />;
      default:
        return <Shield className="h-4 w-4 text-gray-500" />;
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case "critical":
        return "bg-red-100 text-red-800 border-red-200";
      case "high":
        return "bg-orange-100 text-orange-800 border-orange-200";
      case "medium":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "low":
        return "bg-blue-100 text-blue-800 border-blue-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "active":
        return "bg-red-100 text-red-800";
      case "acknowledged":
        return "bg-yellow-100 text-yellow-800";
      case "resolved":
        return "bg-green-100 text-green-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const handleAcknowledge = async (alertId: string) => {
    console.log('Acknowledging alert with ID:', alertId);
    try {
      const response = await fetch(`/api/alerts/${alertId}/acknowledge`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to acknowledge alert: ${response.statusText} - ${errorText}`);
      }

      const result = await response.json();
      console.log('Alert acknowledged successfully:', result);
      // Refresh will happen automatically due to refetchInterval
    } catch (error) {
      console.error('Failed to acknowledge alert:', error);
      alert(`Error: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  };

  const handleResolve = async (alertId: string) => {
    try {
      const response = await fetch(`/api/alerts/${alertId}/resolve`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Failed to resolve alert: ${response.statusText}`);
      }

      console.log('Alert resolved successfully');
      // Refresh will happen automatically due to refetchInterval
    } catch (error) {
      console.error('Failed to resolve alert:', error);
    }
  };

  const alertsList = Array.isArray(alerts) ? alerts : [];
  const filteredAlerts = alertsList.filter((alert: SecurityAlert) => {
    if (filter === "all") return true;
    if (filter === "active") return alert.status === "active";
    if (filter === "critical") return alert.severity === "critical";
    return true;
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background p-6">
        <div className="max-w-6xl mx-auto">
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
            <p className="mt-2 text-muted-foreground">Loading security alerts...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-6" data-testid="alerts-page">
      <div className="max-w-6xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-primary flex items-center gap-3">
              <Bell className="h-8 w-8" />
              Security Alerts
            </h1>
            <p className="text-muted-foreground mt-2">
              Monitor and manage security incidents and compliance violations
            </p>
          </div>

          {/* Filter Buttons */}
          <div className="flex gap-2">
            <Button
              variant={filter === "all" ? "default" : "outline"}
              size="sm"
              onClick={() => setFilter("all")}
              data-testid="filter-all"
            >
              All ({alertsList.length})
            </Button>
            <Button
              variant={filter === "active" ? "default" : "outline"}
              size="sm"
              onClick={() => setFilter("active")}
              data-testid="filter-active"
            >
              Active ({alertsList.filter((a: SecurityAlert) => a.status === "active").length})
            </Button>
            <Button
              variant={filter === "critical" ? "default" : "outline"}
              size="sm"
              onClick={() => setFilter("critical")}
              data-testid="filter-critical"
            >
              Critical ({alertsList.filter((a: SecurityAlert) => a.severity === "critical").length})
            </Button>
          </div>
        </div>

        {/* Alerts List */}
        <div className="space-y-4">
          {filteredAlerts.length === 0 ? (
            <Card>
              <CardContent className="py-8 text-center">
                <Shield className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-semibold mb-2">No alerts found</h3>
                <p className="text-muted-foreground">
                  {filter === "all" 
                    ? "Your system is secure. No alerts have been generated."
                    : `No ${filter} alerts found. Try adjusting your filter.`}
                </p>
              </CardContent>
            </Card>
          ) : (
            filteredAlerts.map((alert: SecurityAlert) => (
              <Alert
                key={alert.id}
                className={`p-6 ${getSeverityColor(alert.severity)}`}
                data-testid={`alert-${alert.id}`}
              >
                <div className="flex items-start justify-between">
                  <div className="flex items-start space-x-3">
                    {getSeverityIcon(alert.severity)}
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h3 className="font-semibold">{alert.title}</h3>
                        <Badge className={getSeverityColor(alert.severity)}>
                          {alert.severity.toUpperCase()}
                        </Badge>
                        <Badge className={getStatusColor(alert.status)}>
                          {alert.status.toUpperCase()}
                        </Badge>
                      </div>
                      <AlertDescription className="mb-3">
                        {alert.description}
                      </AlertDescription>
                      <div className="flex items-center gap-4 text-sm text-muted-foreground">
                        <span>Source: {alert.source}</span>
                        <span>â€¢</span>
                        <span>{new Date(alert.timestamp).toLocaleString()}</span>
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="flex gap-2">
                    {alert.status === "active" && (
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleAcknowledge(alert.id)}
                        data-testid={`acknowledge-${alert.id}`}
                      >
                        Acknowledge
                      </Button>
                    )}
                    {alert.status !== "resolved" && (
                      <Button
                        size="sm"
                        onClick={() => handleResolve(alert.id)}
                        data-testid={`resolve-${alert.id}`}
                      >
                        Resolve
                      </Button>
                    )}
                  </div>
                </div>
              </Alert>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/CompliancePage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { 
  Shield, 
  FileText, 
  AlertTriangle, 
  CheckCircle, 
  Clock,
  Scan,
  Database,
  TrendingUp,
  Eye,
  Settings
} from "lucide-react";

interface Violation {
  type: string;
  subtype: string;
  pattern: string;
  matches: number;
  action: string;
  severity: string;
  source: string;
  content: string;
  timestamp: string;
}

interface LogMetrics {
  totalLogs: number;
  errorRate: number;
  services: {
    FastAPI: number;
    OpenAI: number;
  };
  avgResponseTime: number;
  totalAICost: number;
}

export default function CompliancePage() {
  const [activeTab, setActiveTab] = useState("scanner");
  const [scanText, setScanText] = useState("");
  const [scanSource, setScanSource] = useState("");
  const [scanType, setScanType] = useState("text");
  const [fastApiLog, setFastApiLog] = useState("");
  const [openAiLog, setOpenAiLog] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Queries
  const { data: complianceReport } = useQuery({
    queryKey: ["/api/compliance/report"],
    refetchInterval: 30000,
  });

  const { data: logMetrics } = useQuery({
    queryKey: ["/api/logs/metrics"],
    refetchInterval: 10000,
  });

  const { data: complianceConfig } = useQuery({
    queryKey: ["/api/compliance/config"],
  });

  // Mutations
  const scanMutation = useMutation({
    mutationFn: async (data: { text: string; source: string; type: string }) => {
      const response = await fetch("/api/compliance/scan", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Scan failed");
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Compliance Scan Complete",
        description: `Found ${data.violation_count} violations`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/alerts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/data-classifications"] });
    },
    onError: () => {
      toast({
        title: "Scan Failed",
        description: "Unable to complete compliance scan",
        variant: "destructive",
      });
    },
  });

  const fastApiLogMutation = useMutation({
    mutationFn: async (logData: any) => {
      const response = await fetch("/api/logs/fastapi", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(logData),
      });
      if (!response.ok) throw new Error("Log ingestion failed");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "FastAPI Log Ingested",
        description: "Log successfully processed and analyzed",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/logs/metrics"] });
    },
  });

  const openAiLogMutation = useMutation({
    mutationFn: async (logData: any) => {
      const response = await fetch("/api/logs/openai", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(logData),
      });
      if (!response.ok) throw new Error("Log ingestion failed");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "OpenAI Log Ingested",
        description: "AI usage log processed successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/logs/metrics"] });
    },
  });

  const handleScan = () => {
    if (!scanText || !scanSource) {
      toast({
        title: "Missing Information",
        description: "Please provide both text and source",
        variant: "destructive",
      });
      return;
    }
    scanMutation.mutate({ text: scanText, source: scanSource, type: scanType });
  };

  const handleFastApiLogSubmit = () => {
    try {
      const logData = JSON.parse(fastApiLog);
      fastApiLogMutation.mutate(logData);
      setFastApiLog("");
    } catch (error) {
      toast({
        title: "Invalid JSON",
        description: "Please provide valid JSON log data",
        variant: "destructive",
      });
    }
  };

  const handleOpenAiLogSubmit = () => {
    try {
      const logData = JSON.parse(openAiLog);
      openAiLogMutation.mutate(logData);
      setOpenAiLog("");
    } catch (error) {
      toast({
        title: "Invalid JSON",
        description: "Please provide valid JSON log data",
        variant: "destructive",
      });
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case "critical":
        return "bg-red-100 text-red-800 border-red-200";
      case "high":
        return "bg-orange-100 text-orange-800 border-orange-200";
      case "medium":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "low":
        return "bg-blue-100 text-blue-800 border-blue-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  return (
    <div className="min-h-screen bg-background p-6" data-testid="compliance-page">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-primary flex items-center gap-3">
              <Shield className="h-8 w-8" />
              Advanced Compliance Engine
            </h1>
            <p className="text-muted-foreground mt-2">
              Enhanced PII detection, log ingestion, and real-time compliance monitoring
            </p>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Active Rules</p>
                  <p className="text-2xl font-bold">
                    {complianceReport?.report?.rules_enabled || 0}
                  </p>
                </div>
                <Settings className="h-8 w-8 text-blue-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Logs Processed</p>
                  <p className="text-2xl font-bold">
                    {logMetrics?.metrics?.totalLogs || 0}
                  </p>
                </div>
                <Database className="h-8 w-8 text-green-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">AI Cost</p>
                  <p className="text-2xl font-bold">
                    ${(logMetrics?.metrics?.totalAICost || 0).toFixed(3)}
                  </p>
                </div>
                <TrendingUp className="h-8 w-8 text-purple-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Avg Response</p>
                  <p className="text-2xl font-bold">
                    {Math.round(logMetrics?.metrics?.avgResponseTime || 0)}ms
                  </p>
                </div>
                <Clock className="h-8 w-8 text-orange-500" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Main Content */}
        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="scanner" className="flex items-center gap-2">
              <Scan className="h-4 w-4" />
              Compliance Scanner
            </TabsTrigger>
            <TabsTrigger value="logs" className="flex items-center gap-2">
              <FileText className="h-4 w-4" />
              Log Ingestion
            </TabsTrigger>
            <TabsTrigger value="metrics" className="flex items-center gap-2">
              <TrendingUp className="h-4 w-4" />
              Metrics & Analytics
            </TabsTrigger>
            <TabsTrigger value="config" className="flex items-center gap-2">
              <Settings className="h-4 w-4" />
              Configuration
            </TabsTrigger>
          </TabsList>

          {/* Compliance Scanner Tab */}
          <TabsContent value="scanner" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Scan className="h-5 w-5" />
                  Real-time Compliance Scanner
                </CardTitle>
                <CardDescription>
                  Scan text, transactions, API calls, and AI usage for compliance violations
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="scan-source">Source</Label>
                    <Input
                      id="scan-source"
                      placeholder="e.g., Customer Data, API Response"
                      value={scanSource}
                      onChange={(e) => setScanSource(e.target.value)}
                      data-testid="input-scan-source"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="scan-type">Scan Type</Label>
                    <Select value={scanType} onValueChange={setScanType}>
                      <SelectTrigger data-testid="select-scan-type">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="text">Text Content</SelectItem>
                        <SelectItem value="transaction">Transaction Data</SelectItem>
                        <SelectItem value="api">API Call Data</SelectItem>
                        <SelectItem value="ai">AI Usage Data</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="scan-text">Content to Scan</Label>
                  <Textarea
                    id="scan-text"
                    placeholder="Enter text content, JSON data, or paste logs to scan for compliance violations..."
                    value={scanText}
                    onChange={(e) => setScanText(e.target.value)}
                    rows={6}
                    data-testid="textarea-scan-content"
                  />
                </div>

                <Button 
                  onClick={handleScan} 
                  disabled={scanMutation.isPending}
                  className="w-full"
                  data-testid="button-run-scan"
                >
                  {scanMutation.isPending ? "Scanning..." : "Run Compliance Scan"}
                </Button>

                {/* Example buttons */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-2 pt-4 border-t">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      setScanText("User John Doe with SSN 123-45-6789 and credit card 4532-1234-5678-9012");
                      setScanSource("Customer Database");
                      setScanType("text");
                    }}
                    data-testid="button-example-pii"
                  >
                    Example: PII Data
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      setScanText('{"amount": 15000, "account_id": "acc_123", "recent_count": 75}');
                      setScanSource("Transaction API");
                      setScanType("transaction");
                    }}
                    data-testid="button-example-transaction"
                  >
                    Example: High Volume Transaction
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      setScanText('{"model": "gpt-5", "tokens": {"total": 15000}, "cost": 2.5}');
                      setScanSource("AI Service");
                      setScanType("ai");
                    }}
                    data-testid="button-example-ai"
                  >
                    Example: AI Usage
                  </Button>
                </div>
              </CardContent>
            </Card>

            {/* Recent Scan Results */}
            {scanMutation.data && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Eye className="h-5 w-5" />
                    Scan Results
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex items-center gap-2">
                      <Badge variant="outline">
                        {scanMutation.data.violation_count} violations found
                      </Badge>
                      <Badge variant="outline">
                        Source: {scanMutation.data.source}
                      </Badge>
                      <Badge variant="outline">
                        Type: {scanMutation.data.type}
                      </Badge>
                    </div>

                    {scanMutation.data.violations?.map((violation: Violation, index: number) => (
                      <div key={index} className="border rounded-lg p-4 space-y-2">
                        <div className="flex items-center justify-between">
                          <h4 className="font-medium">
                            {violation.type.replace('_', ' ').toUpperCase()} - {violation.subtype}
                          </h4>
                          <Badge className={getSeverityColor(violation.severity)}>
                            {violation.severity}
                          </Badge>
                        </div>
                        <p className="text-sm text-muted-foreground">
                          {violation.pattern}
                        </p>
                        <p className="text-sm font-mono bg-gray-100 p-2 rounded">
                          {violation.content}
                        </p>
                        <div className="text-xs text-muted-foreground">
                          Action: {violation.action} â€¢ Matches: {violation.matches}
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}
          </TabsContent>

          {/* Log Ingestion Tab */}
          <TabsContent value="logs" className="space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* FastAPI Logs */}
              <Card>
                <CardHeader>
                  <CardTitle>FastAPI Log Ingestion</CardTitle>
                  <CardDescription>
                    Submit API logs for security analysis and compliance checking
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <Textarea
                    placeholder='{"timestamp":"2025-08-31T02:20:00Z","level":"INFO","method":"POST","url":"/api/users","status_code":401,"response_time":150,"user_agent":"suspicious-bot","client_ip":"192.168.1.100"}'
                    value={fastApiLog}
                    onChange={(e) => setFastApiLog(e.target.value)}
                    rows={8}
                    data-testid="textarea-fastapi-log"
                  />
                  <Button 
                    onClick={handleFastApiLogSubmit}
                    disabled={fastApiLogMutation.isPending}
                    className="w-full"
                    data-testid="button-submit-fastapi-log"
                  >
                    {fastApiLogMutation.isPending ? "Processing..." : "Ingest FastAPI Log"}
                  </Button>
                </CardContent>
              </Card>

              {/* OpenAI Logs */}
              <Card>
                <CardHeader>
                  <CardTitle>OpenAI Usage Log Ingestion</CardTitle>
                  <CardDescription>
                    Submit AI usage logs for cost monitoring and compliance
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <Textarea
                    placeholder='{"timestamp":"2025-08-31T02:20:00Z","model":"gpt-5","usage":{"prompt_tokens":1500,"completion_tokens":500,"total_tokens":2000},"duration":3000,"cost":0.15}'
                    value={openAiLog}
                    onChange={(e) => setOpenAiLog(e.target.value)}
                    rows={8}
                    data-testid="textarea-openai-log"
                  />
                  <Button 
                    onClick={handleOpenAiLogSubmit}
                    disabled={openAiLogMutation.isPending}
                    className="w-full"
                    data-testid="button-submit-openai-log"
                  >
                    {openAiLogMutation.isPending ? "Processing..." : "Ingest OpenAI Log"}
                  </Button>
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          {/* Metrics Tab */}
          <TabsContent value="metrics" className="space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card>
                <CardHeader>
                  <CardTitle>Log Processing Metrics</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between">
                      <span>Total Logs Processed:</span>
                      <span className="font-bold">{logMetrics?.metrics?.totalLogs || 0}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Error Rate:</span>
                      <span className="font-bold">
                        {((logMetrics?.metrics?.errorRate || 0) * 100).toFixed(1)}%
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>FastAPI Logs:</span>
                      <span className="font-bold">{logMetrics?.metrics?.services?.FastAPI || 0}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>OpenAI Logs:</span>
                      <span className="font-bold">{logMetrics?.metrics?.services?.OpenAI || 0}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Avg Response Time:</span>
                      <span className="font-bold">
                        {Math.round(logMetrics?.metrics?.avgResponseTime || 0)}ms
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>Total AI Cost:</span>
                      <span className="font-bold">
                        ${(logMetrics?.metrics?.totalAICost || 0).toFixed(4)}
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Compliance Report</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between">
                      <span>Config Version:</span>
                      <span className="font-bold">{complianceReport?.report?.config_version}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Active Rules:</span>
                      <span className="font-bold">{complianceReport?.report?.rules_enabled}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Total Rules:</span>
                      <span className="font-bold">{complianceReport?.report?.total_rules}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Monitoring Status:</span>
                      <Badge className={complianceReport?.report?.monitoring_status === 'active' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}>
                        {complianceReport?.report?.monitoring_status}
                      </Badge>
                    </div>
                    <div className="flex justify-between">
                      <span>Last Updated:</span>
                      <span className="text-sm text-muted-foreground">
                        {complianceReport?.report?.last_updated}
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          {/* Configuration Tab */}
          <TabsContent value="config" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Compliance Configuration</CardTitle>
                <CardDescription>
                  Current compliance rules and monitoring settings
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-6">
                  {complianceConfig?.config?.rules && Object.entries(complianceConfig.config.rules).map(([ruleType, rule]: [string, any]) => (
                    <div key={ruleType} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-medium capitalize">
                          {ruleType.replace('_', ' ')}
                        </h4>
                        <Badge className={rule.enabled ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}>
                          {rule.enabled ? 'Enabled' : 'Disabled'}
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mb-2">
                        Severity: {rule.severity}
                      </p>
                      <div className="text-xs text-muted-foreground">
                        {rule.patterns && `${Object.keys(rule.patterns).length} patterns configured`}
                        {rule.rules && `${Object.keys(rule.rules).length} rules configured`}
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/MonitoringControlPage.t.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { 
  Play, 
  Square, 
  Activity, 
  Settings,
  Shield,
  Eye,
  Zap,
  Clock,
  CheckCircle,
  AlertTriangle,
  Database,
  Cpu
} from "lucide-react";

interface MonitoringStatus {
  isActive: boolean;
  startTime?: string;
  monitoredAPIs: string[];
  totalChecks: number;
  lastActivity: string;
}

export default function MonitoringControlPage() {
  const [monitoringActive, setMonitoringActive] = useState(false);
  const [apiEndpoint, setApiEndpoint] = useState("https://api.plaid.com");
  const [monitoringInterval, setMonitoringInterval] = useState(5);
  const [autoStartEnabled, setAutoStartEnabled] = useState(true);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Simulate monitoring status
  const [monitoringStatus, setMonitoringStatus] = useState<MonitoringStatus>({
    isActive: false,
    monitoredAPIs: [],
    totalChecks: 0,
    lastActivity: "Not started"
  });

  // Auto-refresh monitoring status
  useEffect(() => {
    if (monitoringActive) {
      const interval = setInterval(() => {
        setMonitoringStatus(prev => ({
          ...prev,
          totalChecks: prev.totalChecks + 1,
          lastActivity: new Date().toLocaleTimeString()
        }));
      }, monitoringInterval * 1000);
      return () => clearInterval(interval);
    }
  }, [monitoringActive, monitoringInterval]);

  const startMonitoring = useMutation({
    mutationFn: async () => {
      // Simulate API call to start monitoring
      await new Promise(resolve => setTimeout(resolve, 1000));
      return { success: true };
    },
    onSuccess: () => {
      setMonitoringActive(true);
      setMonitoringStatus({
        isActive: true,
        startTime: new Date().toISOString(),
        monitoredAPIs: [apiEndpoint, "Internal APIs", "Database connections"],
        totalChecks: 0,
        lastActivity: "Monitoring started"
      });
      toast({
        title: "Monitoring Started",
        description: `Security monitoring is now active for ${apiEndpoint}`,
      });

      // Simulate some initial security events
      setTimeout(() => {
        simulateSecurityEvent("PII Detection", "Credit card pattern detected in API response");
      }, 3000);
      setTimeout(() => {
        simulateSecurityEvent("High Volume Alert", "Unusual transaction count detected");
      }, 8000);
    },
  });

  const stopMonitoring = useMutation({
    mutationFn: async () => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { success: true };
    },
    onSuccess: () => {
      setMonitoringActive(false);
      setMonitoringStatus(prev => ({
        ...prev,
        isActive: false,
        lastActivity: "Monitoring stopped"
      }));
      toast({
        title: "Monitoring Stopped",
        description: "Security monitoring has been deactivated",
      });
    },
  });

  const simulateSecurityEvent = (type: string, description: string) => {
    toast({
      title: `ðŸš¨ Security Alert: ${type}`,
      description: description,
      variant: "destructive",
    });
    queryClient.invalidateQueries({ queryKey: ["/api/alerts"] });
  };

  const triggerManualScan = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/compliance/scan", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          text: "Test user data with SSN 123-45-6789 and email john@example.com",
          source: "Manual Security Test",
          type: "text"
        }),
      });
      if (!response.ok) throw new Error("Scan failed");
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Manual Scan Complete",
        description: `Found ${data.violation_count} compliance violations`,
      });
    },
  });

  const ingestTestLog = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/logs/fastapi", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          timestamp: new Date().toISOString(),
          level: "ERROR",
          method: "POST",
          url: "/api/sensitive-data",
          status_code: 401,
          response_time: 2500,
          user_agent: "suspicious-scanner",
          client_ip: "192.168.1.100",
          endpoint: "/api/sensitive-data"
        }),
      });
      if (!response.ok) throw new Error("Log ingestion failed");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Test Log Ingested",
        description: "Suspicious log activity detected and processed",
      });
    },
  });

  return (
    <div className="min-h-screen bg-background p-6" data-testid="monitoring-control-page">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-primary flex items-center gap-3">
              <Activity className="h-8 w-8" />
              Security Monitoring Control Center
            </h1>
            <p className="text-muted-foreground mt-2">
              Start, stop, and manage real-time security monitoring for your applications
            </p>
          </div>
          <div className="flex items-center gap-2">
            <div className={`w-3 h-3 rounded-full ${monitoringActive ? 'bg-green-500 animate-pulse' : 'bg-gray-400'}`}></div>
            <span className="text-sm font-medium">
              {monitoringActive ? 'MONITORING ACTIVE' : 'MONITORING INACTIVE'}
            </span>
          </div>
        </div>

        {/* Control Panel */}
        <Card className="border-2">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Cpu className="h-5 w-5" />
              Monitoring Controls
            </CardTitle>
            <CardDescription>
              Control when and how the security monitoring system operates
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {/* Start/Stop Controls */}
              <div className="space-y-4">
                <h4 className="font-medium">Primary Controls</h4>
                <div className="space-y-3">
                  <Button 
                    onClick={() => startMonitoring.mutate()}
                    disabled={monitoringActive || startMonitoring.isPending}
                    className="w-full bg-green-600 hover:bg-green-700"
                    data-testid="button-start-monitoring"
                  >
                    <Play className="h-4 w-4 mr-2" />
                    {startMonitoring.isPending ? "Starting..." : "Start Monitoring"}
                  </Button>

                  <Button 
                    onClick={() => stopMonitoring.mutate()}
                    disabled={!monitoringActive || stopMonitoring.isPending}
                    variant="destructive"
                    className="w-full"
                    data-testid="button-stop-monitoring"
                  >
                    <Square className="h-4 w-4 mr-2" />
                    {stopMonitoring.isPending ? "Stopping..." : "Stop Monitoring"}
                  </Button>
                </div>
              </div>

              {/* Configuration */}
              <div className="space-y-4">
                <h4 className="font-medium">Configuration</h4>
                <div className="space-y-3">
                  <div>
                    <Label htmlFor="api-endpoint">API Endpoint to Monitor</Label>
                    <Input
                      id="api-endpoint"
                      value={apiEndpoint}
                      onChange={(e) => setApiEndpoint(e.target.value)}
                      placeholder="https://api.example.com"
                      disabled={monitoringActive}
                      data-testid="input-api-endpoint"
                    />
                  </div>

                  <div>
                    <Label htmlFor="monitoring-interval">Check Interval (seconds)</Label>
                    <Input
                      id="monitoring-interval"
                      type="number"
                      value={monitoringInterval}
                      onChange={(e) => setMonitoringInterval(parseInt(e.target.value))}
                      min="1"
                      max="60"
                      disabled={monitoringActive}
                      data-testid="input-monitoring-interval"
                    />
                  </div>
                </div>
              </div>

              {/* Status */}
              <div className="space-y-4">
                <h4 className="font-medium">Current Status</h4>
                <div className="space-y-3">
                  <div className="flex justify-between">
                    <span className="text-sm">Status:</span>
                    <Badge className={monitoringActive ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'}>
                      {monitoringActive ? 'Active' : 'Inactive'}
                    </Badge>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-sm">Checks Run:</span>
                    <span className="text-sm font-mono">{monitoringStatus.totalChecks}</span>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-sm">APIs Monitored:</span>
                    <span className="text-sm font-mono">{monitoringStatus.monitoredAPIs.length}</span>
                  </div>

                  <div className="text-xs text-muted-foreground">
                    Last: {monitoringStatus.lastActivity}
                  </div>
                </div>
              </div>
            </div>

            {/* Advanced Settings */}
            <div className="border-t pt-4">
              <h4 className="font-medium mb-3">Advanced Settings</h4>
              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="auto-start">Auto-start monitoring on system boot</Label>
                  <p className="text-sm text-muted-foreground">Automatically begin monitoring when the application starts</p>
                </div>
                <Switch
                  id="auto-start"
                  checked={autoStartEnabled}
                  onCheckedChange={setAutoStartEnabled}
                  data-testid="switch-auto-start"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Trigger Actions */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Shield className="h-5 w-5" />
                Manual Security Scan
              </CardTitle>
              <CardDescription>
                Trigger an immediate compliance scan with test data
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Button 
                onClick={() => triggerManualScan.mutate()}
                disabled={triggerManualScan.isPending}
                className="w-full"
